let fs = require('fs');
let path = require('path');
let configObj = JSON.parse(fs.readFileSync(__dirname + path.sep + 'config.json', 'utf8'));
console.log(`configObj : ${JSON.stringify(configObj)}`);

module.exports = {
    getConfig :  () => {
        return configObj;
    },
    getSmUrl :  (env) => {
        return configObj[env] || configObj['prod'];
    }
}